import React from "react";
import styled from "styled-components";

import CommentList from "./CommentList";
import Count from "./Count";
import { Grid, Image, Text, Input } from "../../elements";
import heart from "../../static/heart.svg";
import comment from "../../static/comment.svg";

const Post = (props) => {
  console.log(props);

  return (
    <React.Fragment>
      <PostContainer>

        <PostImg>
          <img src={props.image_url} />
        </PostImg>

        <PostBody>

          <Body1Writer>
            <Grid flex>
              <Image shape="circle" src={props.user_info.profile} />
              <Text bold>{props.user_info.nickname}</Text>
            </Grid>
          </Body1Writer>

          <Body2Contents>
            <Text>{props.contents}</Text>
            <Text size="8pt">{props.insert_dt}</Text>
          </Body2Contents>

          <Body3CommentList>
            <CommentList />
          </Body3CommentList>

          <Body4Count>
              <img width="20px" src={heart} />
              <Text blod margin="0px 5px 0px 5px">
                {props.like_cnt}
              </Text>
              <img width="20px" src={comment} />
              <Text blod margin="0px 5px 0px 5px">
                {props.comment_cnt}
              </Text>
          </Body4Count>

          <Body5CommentWrite>
            <input placeholder="댓글 달기" />
          </Body5CommentWrite>

        </PostBody>
      </PostContainer>
    </React.Fragment>
  );
};

Post.defaultProps = {
  id: 0,
  user_info: {
    nickname: "_nickname",
    profile:
      "https://www.zdnet.com/a/hub/i/r/2018/10/18/580b4d43-4060-4d90-be23-1f97ef795e4e/resize/1200x900/757c6793f448336b1a2c73a5d46fb971/github-logo.png",
  },
  image_url:
    "https://media.vlpt.us/images/huurray/post/f827a6c4-7dc5-4239-bd7e-c19352ad8044/javascript.gif",
  contents: "개발자들의 커뮤니티, 깃로그",
  comment_cnt: 8,
  like_cnt: 20,
  insert_dt: "2021-04-12 10:00:00",
};

const PostContainer = styled.div`
  width: 100%;
  margin: auto;
  border: 2px solid #eee;
  box-sizing: border-box;
  display: flex;
`;

const PostImg = styled.div`
  width: 60%;
  aspect-ratio: 1/1;
  overflow: hidden;
`;

const PostBody = styled.div`
  width: 40%;
`;

const Body1Writer = styled.div`
  width: 100%;
  height: 30%;
  border-bottom: 2px solid #eee;
`;

const Body2Contents = styled.div`
  width: 100%;
  border-bottom: 2px solid #eee;
`;

const Body3CommentList = styled.div`
  width: 100%;
  border-bottom: 2px solid #eee;
`;

const Body4Count = styled.div`
  width: 100%;
  height: 15%;
  position: relative;
  bottom: 0;
  border-top: 2px solid #eee;
`;
const Body5CommentWrite = styled.div`
  width: 100%;

`;

export default Post;
