import React from "react";
import styled from "styled-components";

import CommentList from "./CommentList";
import Count from "./Count";
import { Grid, Image, Text, Input } from "../../elements";

const Post = (props) => {
  console.log(props);

  return (
    <React.Fragment>
      <PostContainer>
        <PostImg>
          <img src={props.image_url} />
        </PostImg>

        <PostBody>
          <Body1Contents>
          <Grid flex height="50px">
                <Image shape="circle" src={props.user_info.profile} />
                <Text bold>{props.user_info.nickname}</Text>s
              </Grid>
            <Grid>
              <Text>{props.contents}</Text>
              <Text size="8pt">{props.insert_dt}</Text>
            </Grid>
          </Body1Contents>

          <Body2CommentList>
            <CommentList />
          </Body2CommentList>

          <Body3CommentWrite>
            <Count />
            <input placeholder="댓글 달기" />
          </Body3CommentWrite>
        </PostBody>
      </PostContainer>
    </React.Fragment>
  );
};

Post.defaultProps = {
  id: 0,
  user_info: {
    nickname: "_nickname",
    profile:
      "https://www.zdnet.com/a/hub/i/r/2018/10/18/580b4d43-4060-4d90-be23-1f97ef795e4e/resize/1200x900/757c6793f448336b1a2c73a5d46fb971/github-logo.png",
  },
  image_url:
    "https://media.vlpt.us/images/huurray/post/f827a6c4-7dc5-4239-bd7e-c19352ad8044/javascript.gif",
  contents: "개발자들의 커뮤니티, 깃로그",
  comment_cnt: 8,
  like_cnt: 20,
  insert_dt: "2021-04-12 10:00:00",
};

const PostContainer = styled.div`
  width: 100%;
  margin: auto;
  border: 2px solid #eee;
  box-sizing: border-box;
  display: flex;
`;

const PostImg = styled.div`
  width: 60%;
  aspect-ratio: 1/1;
  overflow: hidden;
`;

const PostBody = styled.div`
  width: 40%;
`;

const Body1Contents = styled.div`
  width: 100%;
  height: 40%;
  border-bottom: 2px solid #eee;
`;

const Body2CommentList = styled.div`
  width: 100%;
  height: 40%;
  border-bottom: 2px solid #eee;
`;

const Body3CommentWrite = styled.div`
  width: 100%;
  height: 15%;
  position: absolute;
  bottom: 0;
`;

export default Post;
