import React from "react";
import styled from "styled-components";

import CommentItem from "./CommentItem";
import Count from "./Count";
import { Grid, Image, Text, Input } from "../../elements";
import heart from "../../static/heart.svg";
import comment from "../../static/comment.svg";

const Post = (props) => {
  console.log(props.user_info.profile);

  return (
    <React.Fragment>
      <PostContainer>
        <PostImg src={props.image_url}></PostImg>

        <PostBody>
          <Body1Writer>
            <Grid flex>
              <ProfileImg src={props.user_info.profile} />
              <Text bold>{props.user_info.nickname}</Text>
            </Grid>
          </Body1Writer>

          <Body2Contents>
            <Text>{props.contents}</Text>
            <Text size="8pt" color="grey">
              {props.insert_dt}
            </Text>
          </Body2Contents>

          <Body3CommentList>
            <CommentItem />
          </Body3CommentList>

          <Body4Count>
              <Icon src={heart} />
              <Text blod margin="0px 5px 0px 5px">
                {props.like_cnt}
              </Text>
              <Icon src={comment} />
              <Text blod margin="0px 5px 0px 5px">
                {props.comment_cnt}
              </Text>
          </Body4Count>

          <Body5CommentWrite>
            <CommentInput placeholder="댓글 달기" />
          </Body5CommentWrite>
        </PostBody>
      </PostContainer>
    </React.Fragment>
  );
};

Post.defaultProps = {
  id: 0,
  user_info: {
    nickname: "_nickname",
    profile:
      "https://timesofindia.indiatimes.com/photo/67586673.cms",
  },
  image_url:
    "https://media.vlpt.us/images/huurray/post/f827a6c4-7dc5-4239-bd7e-c19352ad8044/javascript.gif",
  contents: "개발자들의 커뮤니티, 깃로그",
  comment_cnt: 8,
  like_cnt: 20,
  insert_dt: "2021-04-12 10:00:00",
};

const PostContainer = styled.div`
  width: 100%;
  margin: auto;
  border: 2px solid #eee;
  box-sizing: border-box;
  display: flex;
`;

const PostImg = styled.div`
  width: 60%;
  height: auto;
  aspect-ratio: 1/1;
  overflow: hidden;
  object-fit: cover;
  object-position: 50% 50% ;
  background-image: url("${(props) => props.src}");
  background-size: cover;
`;

const PostBody = styled.div`
  width: 40%;
`;

const Body1Writer = styled.div`
  box-sizing: border-box;
  width: 100%;
  height: 8%;
  background-color: #eee;
  padding-left: 10px;
`;

const Body2Contents = styled.div`
  box-sizing: border-box;
  width: 100%;
  height: 30%;
  border-bottom: 2px solid #eee;
  overflow: hidden;
  padding-left: 10px;
`;

const Body3CommentList = styled.div`
  box-sizing: border-box;
  width: 100%;
  height: 48%;
  overflow: hidden;
  background-color: #eee;
  padding: 10px;
`;

const Body4Count = styled.div`
  box-sizing: border-box;
  width: 100%;
  height: 7%;
  border-top: 2px solid #eee;
  align-items: center;
  display: flex;
  padding: 10px;
`;
const Body5CommentWrite = styled.div`
  box-sizing: border-box;
  width: 100%;
  height: 7%;
  background-color: #eee;
`;

const ProfileImg = styled.div`
  width: 8%;
  height: auto;
  aspect-ratio: 1/1;
  border-radius: 50%;
  background-color: grey;
  object-fit: cover;
`;

const Icon = styled.img`
  margin: 0px 5px 0px 5px;
  width: 16px;
`;

const CommentInput = styled.input`
  background-color: none;
  outline: none;
  border: none;
  margin: 10px;
`;

export default Post;
